import React, { useState, useEffect } from "react";
import {
  Button,
  Card,
  CardBody,
  Container,
  Input,
  Label,
  Modal,
  ModalBody,
  ModalFooter,
  ModalHeader,
} from "reactstrap";

// Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb";
import TableContainer from "../../../components/Common/TableContainer";
import {
  DELETE_LEAVE,
  DELETE_USER,
  GET_ITEM_MAKER,
  GET_ITEM_SUB_CATEGORY,
  GET_LEAD_REFERENCE,
  GET_LEAVE,
  GET_MARKETING_LEAD,
  GET_SHIFT,
  GET_UNIT,
  GET_UNIT_MEASURE,
  GET_WAREHOUSE,
  UPDATE_INQUIRY_LEAD_MARKETING,
  UPDATE_ITEM_SUB_CATGEORY_STATUS,
  UPDATE_LEAD_MARKETING_STATUS,
  UPDATE_LEAD_REFERENCE_STATUS,
  UPDATE_LEAVE_STATUS,
  UPDATE_SHIFT_STATUS,
  UPDATE_UNIT_MEASURE_STATUS,
  UPDATE_UNIT_STATUS,
  UPDATE_WAREHOUSE_STATUS,
} from "../../../globals";
import TableComponent from "../../../components/Common/tablecomponent";
import { ThreeDots } from "react-loader-spinner";
import { toast } from "react-toastify";
import CloseIcon from "@mui/icons-material/Close";

const GetMarketingLead = () => {
  const [breadcrumbItems] = useState([
    { title: "Master", link: process.env.PUBLIC_URL + "/dashboard" },
    {
      title: "Marketing Lead",
      link: process.env.PUBLIC_URL + "/marketingleadlist",
    },
  ]);

  const [page, setPage] = useState(1);
  const [isLoading, setisLoading] = useState(false);
  const [userlist, setUserlist] = useState([]);
  const [totalPages, setTotalPages] = useState("");
  const [currentpageIndex, setCurrentpageIndex] = useState("");
  const [perPage, setPerPage] = useState("");
  const [show, setShow] = useState(false);
  const [convertStatusModal, setConvertStatusModal] = useState(false);
  const [notes, setNotes] = useState(false);

  console.log("convertStatusModal ===>", convertStatusModal);
  const [cancelInquiryStatusModal, setCancelInquiryStatusModal] =
    useState(false);
  //   cancelInquiryStatus
  const [isUserID, setIsUserID] = useState(false);
  const [isConvertInquiry, setIsConvertInquiry] = useState({});
  const [CreateButton, setCreateButton] = useState("Active");
  const [EditButton, setEditButton] = useState("Active");
  const [DeleteButton, setDeleteButton] = useState([]);
  const PermissionArr =
    JSON.parse(localStorage.getItem("permissionarray")) || [];

  const sidebarArr = [
    {
      href: "hsncodelist",
      value: "hsn_code",
      label: "HSN Code",
    },
    {
      href: "countrylist",
      value: "country",
      label: "Country",
    },
    {
      href: "statelist",
      value: "state",
      label: "State",
    },
    {
      href: "ledgerlist",
      value: "ledger",
      label: "Ledger",
    },
    {
      href: "ledgergrouplist",
      value: "ledger_group",
      label: "Ledger Group",
    },
    {
      href: "termsandconditionslist",
      value: "terms_condition",
      label: "Terms & Conditions",
    },
    {
      href: "departmentlist",
      value: "department",
      label: "Department",
    },
    {
      href: "designationlist",
      value: "designation",
      label: "Designation",
    },
    {
      href: "itemcategorylist",
      value: "item_category",
      label: "Item Category",
    },
    {
      href: "itemsubcategorylist",
      value: "item_sub_category",
      label: "Item Sub-Category",
    },
    {
      href: "itemmakelist",
      value: "item_make",
      label: "Item Make",
    },
    {
      href: "itemlist",
      value: "item",
      label: "Item",
    },
    {
      href: "shiftlist",
      value: "shift",
      label: "Shift",
    },
    {
      href: "unitlist",
      value: "unit",
      label: "Unit",
    },
    {
      href: "unitmeasurelist",
      value: "unit_measure",
      label: "Unit Measure",
    },
    {
      href: "warehouselist",
      value: "warehouse",
      label: "Warehouse",
    },
    {
      href: "leavelist",
      value: "leave",
      label: "Leave",
    },
    {
      href: "leavebalancelist",
      value: "leave_balances",
      label: "Leave Balance",
    },
    {
      href: "leaverequestlist",
      value: "leave_request",
      label: "Leave Request",
    },
  ];

  const columns = [
    { Header: "Id", accessor: "Id", disableFilters: true, filterable: false },
    // ... (other columns)
  ];

  useEffect(() => {
    getAllUsers();
    // loadCreatePermissions();
    // loadEditPermissions();
    // loadDeletePermissions();
  }, []); // Run the effect when the page changes

  const loadCreatePermissions = async () => {
    try {
      const tempArr = PermissionArr.filter((item) =>
        item.name.includes("Add Leave Master")
      ).flatMap((item) =>
        sidebarArr
          .filter((sidebar) => item.type === sidebar.value)
          .map((sidebar) => ({
            active: item.status,
          }))
      );
      const finalString = tempArr.map((item) => item.active).join(", ");
      setCreateButton(finalString);
    } catch (error) {}
  };

  const loadEditPermissions = async () => {
    try {
      const tempArr = PermissionArr.filter((item) =>
        item.name.includes("Edit Leave Master")
      ).flatMap((item) =>
        sidebarArr
          .filter((sidebar) => item.type === sidebar.value)
          .map((sidebar) => ({
            active: item.status,
          }))
      );
      const finalString = tempArr.map((item) => item.active).join(", ");
      setEditButton(finalString);
    } catch (error) {}
  };

  const loadDeletePermissions = async () => {
    try {
      const tempArr = PermissionArr.filter((item) =>
        item.name.includes("Delete Warehouse Master ")
      ).flatMap((item) =>
        sidebarArr
          .filter((sidebar) => item.type === sidebar.value)
          .map((sidebar) => ({
            active: item.status,
          }))
      );
      const finalString = tempArr.map((item) => item.active).join(", ");
      setDeleteButton(finalString);
    } catch (error) {}
  };

  const getAllUsers = async (page, perPage) => {
    setisLoading(true);
    var Token = await localStorage.getItem("userToken");
    try {
      const response = await fetch(
        page ? `${GET_MARKETING_LEAD}?page=${page}` : `${GET_MARKETING_LEAD}`,
        {
          method: "GET",
          headers: {
            Authorization: "Bearer " + Token,
            "Content-Type": "application/json",
          },
        }
      );
      const data = await response.json();
      if (data.result === true) {
        setUserlist(data.data);
        setisLoading(false);
        setTotalPages(data.meta.pagination.total_pages);
        setCurrentpageIndex(data.meta.pagination.current_page);
        setPerPage(data.meta.pagination.per_page);
      } else {
        setisLoading(false);
      }
    } catch (error) {
      setisLoading(false);
    }
  };

  const DeleteUserData = async (isUserID) => {
    setisLoading(true);
    var Token = localStorage.getItem("userToken");
    await fetch(DELETE_LEAVE + isUserID, {
      method: "POST",
      headers: {
        Authorization: "Bearer " + Token,
      },
    })
      .then((res) => {
        return res.json();
      })

      .then((res) => {
        toast("Leave Deleted !", {
          type: "success",
        });

        setShow(false);
        setisLoading(false);
        getAllUsers();
      })
      .catch((err) => {
        toast("Unable to Delete Leave", {
          type: "error",
        });

        setShow(false);
        setisLoading(false);
      });
  };

  // UPDATE SATATUS FROM SWITCH
  const updateContactStatus = async (userid, status) => {
    setisLoading(true);
    var Token = localStorage.getItem("userToken");
    await fetch(UPDATE_LEAD_MARKETING_STATUS + userid, {
      // UPDATE_USER_STATUS + userid,
      // {
      //     method: "POST",
      //     headers: {
      //         Authorization: "Bearer " + Token,
      //     },
      //     body: JSON.stringify({
      //         status: status == true ? "Active" : "Inactive",
      //     }),
      // }
      method: "POST",
      headers: {
        Authorization: "Bearer " + Token,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        status: status == true ? "Active" : "Inactive",
      }),
    })
      .then((response) => {
        response.json().then((data) => {
          if (data.result == true) {
            toast("Marketing Lead Updated Successfully !", {
              type: "success",
            });
            getAllUsers();
            setShow(false);
            setisLoading(false);
          } else {
            getAllUsers();
            setisLoading(false);
            toast("Marketing Lead Cannot Be Updated", {
              type: "error",
            });
          }
        });
      })
      .catch((err) => {
        toast("Unable to Update Marketing Lead", {
          type: "error",
        });
        setShow(false);
        setisLoading(false);
      });
  };

  // UPDATE BY Inquiry Status
  const inquiryVerifiedStatus = async (id, value) => {
    var Token = await localStorage.getItem("userToken");
    // return false
    try {
      fetch(UPDATE_INQUIRY_LEAD_MARKETING + isConvertInquiry.id, {
        method: "POST",
        headers: {
          Authorization: "Bearer " + Token,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          lead_conversion_status: "1", // 1= covver
        }),
      }).then((response) => {
        response.json().then((data) => {
          if (data.result == true) {
            toast("SuccessFully Update Status", {
              type: "success",
            });
            getAllUsers(currentpageIndex, perPage);
            setConvertStatusModal(false);
          }
        });
      });
    } catch (error) {
      // console.log("Something Went Wrong", error);
    }
  };

  // UPDATE BY Inquiry Status cancel
  const cancelInquiryStatus = async (id, value) => {
    var Token = await localStorage.getItem("userToken");
    // return false
    try {
      fetch(UPDATE_INQUIRY_LEAD_MARKETING + isConvertInquiry.id, {
        method: "POST",
        headers: {
          Authorization: "Bearer " + Token,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          lead_conversion_status: "0", // 1= covver
          cancled_reason: notes,
        }),
      }).then((response) => {
        response.json().then((data) => {
          if (data.result == true) {
            toast("SuccessFully Update Status", {
              type: "success",
            });

            getAllUsers(currentpageIndex, perPage);
            setCancelInquiryStatusModal(false);
          }
        });
      });
    } catch (error) {
      // console.log("Something Went Wrong", error);
    }
  };

  // TABLE HEADER
  const renderTHContent = () => (
    <tr>
      {EditButton == "Active" || DeleteButton == "Active" ? (
        <th className="text-center">Action</th>
      ) : (
        ""
      )}
      <th>Lead No</th>
      <th>Inquiry Convert</th>
      <th>Lead Type</th>
      <th>Lead Nature</th>
      <th>Reference Person</th>
      <th>Contact Person</th>
      <th>Lead Status</th>
    </tr>
  );

  // TABLE DATA
  function renderTdContent(item) {
    return (
      <>
        <td>
          <h5 className="font-size-14 text-truncate"></h5>
          <p className="mb-0">
            <span className="fw-medium">{item.lead_no}</span>
          </p>
        </td>
        <td>
          <h5 className="font-size-14 text-truncate"></h5>
          <p className="mb-0">
            {item.lead_conversion_status == "0" ? (
              <>
                {" "}
                <Button
                  color="success"
                  outline
                  size="sm"
                  onClick={() => {
                    setIsConvertInquiry(item);
                    setConvertStatusModal(true);
                  }}
                >
                  Convert
                </Button>
              </>
            ) : (
              <>
                <Button
                  color="danger"
                  outline
                  size="sm"
                  className="px-4"
                  onClick={() => {
                    setIsConvertInquiry(item);
                    setCancelInquiryStatusModal(true);
                  }}
                >
                  <i className="mdi mdi-close-thick"></i>
                </Button>
              </>
            )}
          </p>
        </td>
        <td>
          <h5 className="font-size-14 text-truncate"></h5>
          <p className="mb-0">
            <span className="fw-medium">{item.lead_type}</span>
          </p>
        </td>
        <td>
          <h5 className="font-size-14 text-truncate"></h5>
          <p className="mb-0">
            <span className="fw-medium">{item.nature_of_lead}</span>
          </p>
        </td>
        <td>
          <h5 className="font-size-14 text-truncate"></h5>
          <p className="mb-0">
            <span className="fw-medium">{item.lead_reference_name}</span>
          </p>
        </td>
        <td>
          <h5 className="font-size-14 text-truncate"></h5>
          <p className="mb-0">
            <span className="fw-medium">{item.contact_person_name}</span>
          </p>
        </td>
        <td>
          <h5 className="font-size-14 text-truncate"></h5>
          <p className="mb-0">
            <span className="fw-medium">{item.lead_status}</span>
          </p>
        </td>
      </>
    );
  }

  // Start------------RolePermission for Button
  // Retrieve data from localStorage
  const permissionsString = localStorage.getItem("permissionarray");

  // Parse the JSON string into a JavaScript object
  const permissions = JSON.parse(permissionsString);
  // this is use for all active permission
  const filteredPermissions = permissions.filter(
    (permission) => permission.status === "Active"
  );
  const AddMarketingLead = filteredPermissions?.find(
    (permission) => permission.name === "Add Marketing_lead Master"
  );
  const EditMarketingLead = filteredPermissions?.find(
    (permission) => permission.name === "Edit Marketing_lead Master"
  );
  // console.log(EditLeadReference, "====>EditLeadReference");
  // End------------RolePermission for Button

  return (
    <React.Fragment>
      <div className="page-content">
        <Container fluid>
          <Breadcrumbs
            title="Marketing Lead list"
            breadcrumbItems={breadcrumbItems}
          />

          <CardBody>
            {isLoading ? (
              <>
                <ThreeDots
                  height="80"
                  width="80"
                  radius="9"
                  color="#4D5DC6"
                  ariaLabel="three-dots-loading"
                  wrapperStyle={{
                    justifyContent: "center",
                  }}
                  wrapperClassName=""
                  visible={true}
                />
              </>
            ) : (
              // <TableContainer
              <TableComponent
                approveButton={"Active"}
                // approvelink={"/viewmarketlead1/"}
                approvelink={"/viewmarketlead2/"}
                allUserData={userlist || []}
                perPage={perPage}
                currentpageIndex={currentpageIndex}
                pageTotal={totalPages}
                setPerPage={perPage}
                setIsUserID={setIsUserID}
                setShow={setShow}
                getAllUsers={getAllUsers}
                renderTHContent={renderTHContent}
                renderTdContent={renderTdContent}
                btnName="Add Marketing Lead"
                // addData={CreateButton == "Active" ? "/createmarketlead" : ""}
                addData={
                  AddMarketingLead?.status == "Active"
                    ? "/createmarketleadwizard"
                    : ""
                }
                editData={
                  EditMarketingLead?.status == "Active"
                    ? "/editmarketlead/"
                    : ""
                }
                MasterSearch={"terms_condition_type"}
                deletedata={DeleteButton == "Active" ? "Active" : ""}
                // emailSearch1={'email'}
              />
            )}
          </CardBody>
        </Container>
      </div>
      <Modal isOpen={show} backdrop="static">
        <ModalHeader toggle={() => setShow(false)}>
          Delete Confirmation
        </ModalHeader>
        <ModalBody>
          <p>Are you sure you want to delete this Marketing Lead?</p>
        </ModalBody>
        <ModalFooter>
          <Button type="button" color="light" onClick={() => setShow(false)}>
            Close
          </Button>
          <Button
            type="button"
            color="primary"
            onClick={() => DeleteUserData(isUserID)}
          >
            Delete
          </Button>
        </ModalFooter>
      </Modal>
      {convertStatusModal && (
        <Modal isOpen={convertStatusModal} backdrop="static">
          <ModalHeader toggle={() => setConvertStatusModal(false)}>
            Convert Confirmation
          </ModalHeader>
          <ModalBody>
            <p>
              Are you sure you want to Convert this Marketing Lead to Inquiry?
            </p>
          </ModalBody>
          <ModalFooter>
            <Button
              type="button"
              color="primary"
              onClick={() => inquiryVerifiedStatus()}
            >
              Yes
            </Button>
            <Button
              type="button"
              color="light"
              onClick={() => setConvertStatusModal(false)}
            >
              No
            </Button>
          </ModalFooter>
        </Modal>
      )}

      <Modal isOpen={cancelInquiryStatusModal} backdrop="static">
        <ModalHeader toggle={() => setCancelInquiryStatusModal(false)}>
          Convert Confirmation
        </ModalHeader>
        <ModalBody>
          <p>Are you sure you want to cancel Inquiry ?</p>
          <div className="mt-3">
            <Label>Add Reason</Label>
            <Input
              type="text"
              placeholder="Enter Reason"
              onChange={(e) => setNotes(e.target.value)}
            />
          </div>
        </ModalBody>
        <ModalFooter>
          <Button
            type="button"
            color="primary"
            onClick={() => cancelInquiryStatus()}
          >
            Yes
          </Button>
          <Button
            type="button"
            color="light"
            onClick={() => setCancelInquiryStatusModal(false)}
          >
            No
          </Button>
        </ModalFooter>
      </Modal>
    </React.Fragment>
  );
};

export default GetMarketingLead;
